    // get sevice fee percentage
    if(!function_exists('serviceFee')){
        function serviceFee(){
            $deductionPercentage;
            DB::table('tb_fee')->first()!=null ? $deductionPercentage = DB::table('tb_fee')->first()->fee : $deductionPercentage = 20; 
            return $deductionPercentage; 
        }
    }

    // check image exist or not
    if(function_exists('imageExists')){
        function imageExists($filePath)
        {
            $header_response = get_headers($filePath, 1);
            return strpos($header_response[0], "404") !== false ? false:true;
        }
    }

    if(!function_exists('getTimeZoneTime'))
    {
        function getTimeZoneTime($ftz,$ttz,$time)
        {
            date_default_timezone_set($ftz);
            $datetime = new DateTime($time);
            $la_time = new DateTimeZone($ttz);
            $datetime->setTimezone($la_time);
            return date("g:i A", strtotime($datetime->format('H:i')));
        }
    }

    if(!function_exists('getTimeZoneDate'))
    {
        function getTimeZoneDate($ftz,$ttz,$date)
        {
            date_default_timezone_set($ftz);
            $datetime = new DateTime($date);
            $la_time = new DateTimeZone($ttz);
            $datetime->setTimezone($la_time);
            return date("d M Y", strtotime($date));
        }
    }

    if(!function_exists('getCurrentUserTimeZone'))
    {
        function getCurrentUserTimeZone(){
            if(config('app.live_ip_check')=='local')
            {
                $ip = file_get_contents("http://ipecho.net/plain");
                $url = 'http://ip-api.com/json/'.$ip;
                $tz = file_get_contents($url);
                $tz = json_decode($tz,true)['timezone'];
                return $tz;
            }
            else if(config('app.live_ip_check')=='live')
            {
                $ip = \Request::getClientIp(true);
                $url = 'http://ip-api.com/json/'.$ip;
                $curl = curl_init();
                curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
                curl_setopt($curl, CURLOPT_URL, $url);
                curl_setopt($curl, CURLOPT_CUSTOMREQUEST, 'GET');
                curl_setopt($curl, CURLOPT_HTTPHEADER, array("content-type: application/json"));
                $response = curl_exec($curl);
                curl_close($curl);
                $tz = json_decode($response,true)['timezone'];
                return $tz;
            }
            
        }
    }

    if(!function_exists('categories'))
    {
        function categories()
        {
            return Category::whereNotIn('title',['all','All'])->inRandomOrder()->get();
        }
    }

    if(!function_exists('subCategories'))
    {
        function subCategories()
        {
            return Category::where('is_subcategory','!=','0')->whereNotIn('title',['all','All'])->inRandomOrder()->get();
        }
    }

    if(!function_exists('parentCategories'))
    {
        function parentCategories()
        {
            return Category::where('is_subcategory','0')->whereNotIn('title',['all','All'])->inRandomOrder()->get();
        }
    }

