    if(!function_exists('deviceTime'))
    {
        function deviceTime($dateFormatString)
        { 
            $responseTime = 21;
            $tz = getCurrentUserTimeZone();
            $date = new DateTime(date('m/d/Y h:i:s a', time()));
            $date->setTimezone(new DateTimeZone($tz));
            return $date->format($dateFormatString);
        }
    }

    if(!function_exists('appointmentCount')){
        function appointmentCount()
        {
        if(Auth::check()){
            if (Auth::user()->type == 'seller') {
                $appointments = Booking::where('seller_id', Auth::user()->id)->where('state','0')->get();
            } else {
                $appointments = Booking::where('buyer_id', Auth::user()->id)->where('state', '0')->get();
            }

            return ['appointment_count'=>count($appointments) ,'appointments'=> $appointments];
            
            }
        }
    }
